import { WebAppComponent } from "../component.js";
import { BASE_URL } from "../config.js";
export class DatePicker extends WebAppComponent {
    constructor(props, config) {
        const defaultConfig = {
            baseUrl: BASE_URL,
            path: "date-picker",
        };
        super(props, Object.assign(defaultConfig, config));
    }
    static transform({ date }) {
        return {
            type: "date",
            date: new Date(date),
        };
    }
    static match(filter = () => true) {
        return (ctx) => ctx.webAppDataRaw?.type === "date" &&
            ctx.webAppData?.type === "date" &&
            filter(ctx);
    }
}
