import { WebAppComponent } from "../component.js";
import { BASE_URL } from "../config.js";
export class QrScanner extends WebAppComponent {
    constructor(props, config) {
        const defaultConfig = {
            baseUrl: BASE_URL,
            path: "qr-scanner",
        };
        super(props, Object.assign(defaultConfig, config));
    }
    setSendButtonText(value) {
        return this.setProp("sendButtonText", value);
    }
    static transform(data) {
        return data;
    }
    static match(filter = () => true) {
        return (ctx) => ctx.webAppDataRaw?.type === "qr" &&
            ctx.webAppData?.type === "qr" &&
            filter(ctx);
    }
}
