import { WebAppComponent } from "../component.js";
import { BASE_URL } from "../config.js";
export class TimePicker extends WebAppComponent {
    constructor(props, config) {
        const defaultConfig = {
            baseUrl: BASE_URL,
            path: "time-picker",
        };
        super(props, Object.assign(defaultConfig, config));
    }
    static transform({ time, timeZone, }) {
        return {
            type: "time",
            utcTime: new Date(new Date(`1970-01-01T${time}:00Z`).toLocaleString("en-US", {
                timeZone,
            })),
            time: new Date(`1970-01-01T${time}:00Z`),
            timeZone,
        };
    }
    static match(filter = () => true) {
        return (ctx) => ctx.webAppDataRaw?.type === "time" &&
            ctx.webAppData?.type === "time" &&
            filter(ctx);
    }
}
